import { expect } from "chai";
import { isRequired } from "../index.js";
describe("Test isRequired", function () {
  it("basic test", function () {
    var a = function a() {
      var foo = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : isRequired("foo");
    };

    expect(a).to["throw"]("foo");

    var b = function b() {
      return a("");
    };

    expect(b).to.not["throw"]();
  });
});