"use strict";

var _chai = require("chai");

var _queryFrom = require("../queryFrom");

describe("Test query all", function () {
  after(function () {
    return document.body.innerHTML = "";
  });
  it("test super queryAll", function () {
    document.body.innerHTML = "\n<main id=\"root\"><div></div><span></span></main>\n";

    var acture = _queryFrom.defaultQuery.all(['main', 'div', 'span']);

    (0, _chai.expect)(acture.length).to.be.equal(3);
  });
  it("test query nothing", function () {
    var acture1 = _queryFrom.defaultQuery.all(null);

    var acture2 = _queryFrom.defaultQuery.all(undefined);

    var acture3 = _queryFrom.defaultQuery.all();

    (0, _chai.expect)([acture1, acture2, acture3]).to.deep.equal([null, undefined, undefined]);
  });
});