import { expect } from "chai";
import cssNumberToUnit from "../cssNumberToUnit";
describe("Test cssNumberToUnit", function () {
  it("basic test", function () {
    var actual = cssNumberToUnit("width", 1);
    expect(actual).to.equal("1px");
  });
  it("test value with array", function () {
    var actual = cssNumberToUnit("width", [1, 1]);
    expect(actual).to.deep.equal(["1px", "1px"]);
  });
  it("test not append px", function () {
    var actual = cssNumberToUnit("lineHeight", 1);
    expect(actual).to.equal(1);
  });
});